import requests
from gmpy2 import next_prime
from Crypto.Util.number import inverse, long_to_bytes

def factorize(n):
    l = []
    url="http://factordb.com/api?query="+str(n)
    r = requests.get(url)
    data = r.json()
    for factor in data['factors']:
        l.append(int(factor[0]))
    return l

